import VendorType from '../types/VendorType';
import Vendor from '../../data/models/Vendor';
import VendorInputType from '../types/VendorInputType';
import bcrypt from 'bcrypt';
import User from "../../data/models/User";

function stringToSlug (str) {
  str = str.replace(/^\s+|\s+$/g, ''); // trim
  str = str.toLowerCase();

  // remove accents, swap ñ for n, etc
  var from = "àáäâèéëêìíïîòóöôùúüûñç·/_,:;";
  var to   = "aaaaeeeeiiiioooouuuunc------";
  for (var i=0, l=from.length ; i<l ; i++) {
    str = str.replace(new RegExp(from.charAt(i), 'g'), to.charAt(i));
  }

  str = str.replace(/[^a-z0-9 -]/g, '') // remove invalid chars
    .replace(/\s+/g, '-') // collapse whitespace and replace by -
    .replace(/-+/g, '-'); // collapse dashes

  return str;
}

const newVendor = {
  type: VendorType,
  args: {
    vendor: { type: VendorInputType },
  },
  resolve(value, { vendor }) {
    let slug = stringToSlug(vendor.dbaName);

    //split address
    let streetAddress =  'address from address';
    let city = 'city from address';
    let state = 'state from address';
    let zip = 'zip from address';
    let long = '25.4';
    let lat = '25.2';

    Vendor.create({
      slug: slug,
      dbaName: vendor.dbaName,
      legalEntityName: vendor.legalEntityName,
      physicalAddress: vendor.physicalAddress,
      physicalStreetAddress: streetAddress,
      physicalCity: city,
      physicalState: state,
      physicalZipCode: zip,
      latitude: lat,
      longitude: long,
      alcoholLicenseNumber: vendor.alcoholLicenseNumber,
      alcoholLicenseIssuingAgency: vendor.alcoholLicenseIssuingAgency,
      alcoholLicenseExpiration: vendor.alcoholLicenseExpiration,
      doesDelivery: true, //need to impute these
      doesPickup: false,
      deliveryRadius: vendor.deliveryRadius,
      vendorImage: '',
    }).then( (newVendor) => {
      console.log(vendor)
      const hash = bcrypt.hashSync(vendor.adminPassword, 10);
      User.create({
        name: vendor.adminName,
        email: vendor.adminEmail,
        phone: vendor.adminPhone,
        password: hash}).then( (user) => {
        newVendor.addUser(user);
        return newVendor;
      })
    });
  },
};

export default newVendor;
